// --------- BEGIN CMS ---------
var formData = new FormData();
formData.append('_token', document.querySelector('meta[name="csrf-token"]').content);
window.addEventListener('DOMContentLoaded', function () {
  fetch('/cms', {
    method: 'POST',
    body: formData
  }).then(function (response) {
    return response.json();
  }).then(function (data) {
    document.querySelectorAll('img[data-cms]').forEach(function (item) {
      if (data.hasOwnProperty(item.dataset.cms)) {
        item.src = data[item.dataset.cms];

        if (item.dataset.scroll) {
          item.dataset.scroll = data[item.dataset.cms];
        } else if (item.dataset.load) {
          item.dataset.load = data[item.dataset.cms];
        }

        item.onload = function () {
          item.style.visibility = 'visible';
        };

        item.onerror = function () {
          item.style.visibility = 'visible';
        };
      } else {
        item.style.visibility = 'visible';
      }
    });
    document.querySelectorAll('[data-cms]:not(img)').forEach(function (item) {
      if (data.hasOwnProperty(item.dataset.cms)) {
        if (data[item.dataset.cms]) {
          item.innerText = data[item.dataset.cms];
        } else {
          item.innerHTML = '&nbsp;';
        }

        var href = item.getAttribute('href');

        if (item.tagName.toLowerCase() === 'a' && !href.startsWith('http') && !href.startsWith('#') && !href.startsWith('/')) {
          item.href = getHref(data[item.dataset.cms]);
        }
      }

      item.style.visibility = 'visible';
    });
    document.querySelectorAll('[data-cms-clone]').forEach(function (item) {
      if (data.hasOwnProperty(item.dataset.cmsClone)) {
        item.href = data[item.dataset.cmsClone];
      }
    });
    document.querySelectorAll('[data-cms-background]').forEach(function (item) {
      if (data.hasOwnProperty(item.dataset.cmsBackground)) {
        item.style.backgroundImage = "url(".concat(data[item.dataset.cmsBackground], ")");
      }

      item.style.visibility = 'visible';
    });
  });
}); // --------- END CMS ---------
// --------- BEGIN CUSTOMER REGISTER ---------

window.addEventListener('DOMContentLoaded', function () {
  document.querySelectorAll('.js-form').forEach(function (item) {
    item.addEventListener('submit', function (event) {
      event.preventDefault();
      var registerForm = new FormData(item);
      registerForm.append('_token', document.querySelector('meta[name="csrf-token"]').content);

      for (var i = 0; i < item.elements.length; i++) {
        item[i].disabled = true;
      }

      setText(item, Language.sending);
      var action = item.getAttribute('action') ? item.getAttribute('action') : '/customer';
      var method = item.getAttribute('method') ? item.getAttribute('method') : 'post';
      fetch(action, {
        method: method,
        body: registerForm
      }).then(function (response) {
        if (!response.ok) {
          throw Error(response.statusText);
        }

        return response.text();
      }).then(function (data) {
        setText(item, Language.sentSuccessfully);
      })["catch"](function (error) {
        setText(item, Language.sentFailed);
      });
    });
  });
});

function setText(item, data) {
  var $submit = item.querySelector('[type=submit]');

  if ($submit.tagName.toLowerCase() === 'input') {
    $submit.value = data;
  } else {
    var span = $submit.querySelector('span');

    if (span) {
      span.innerText = data;
    }
  }
} // --------- END CUSTOMER REGISTER ---------


function getHref(content) {
  if (content) {
    content = content.trim();
    var emailPattern = /^.{1,30}@.+\..{1,25}$/;

    if (emailPattern.test(content)) {
      return 'mailto:' + content;
    }

    if (content.length < 25) {
      var phone = content.replace(/[^+0-9]/g, '');

      if (phone.length > 9) {
        return 'tel:' + phone;
      }
    }

    if (content.length < 30 && content.startsWith('http')) {
      return content;
    }
  }

  return 'javascript:void(0)';
} // --------- BEGIN LAZY LOAD IMAGE ---------


window.onload = loadImage;

function loadImage() {
  document.querySelectorAll('img[data-load]').forEach(function (item) {
    item.src = item.dataset.load;
  });
}

var fired = false;
window.addEventListener("scroll", function () {
  if (fired === false && (document.documentElement.scrollTop != 0 || document.body.scrollTop != 0)) {
    document.querySelectorAll('img[data-scroll]').forEach(function (item) {
      item.src = item.dataset.scroll;
    });
    fired = true;
  }
}, true); // --------- END LAZY LOAD IMAGE ---------

